import React, { useState, useEffect } from 'react';
import Navbar from 'react-bootstrap/Navbar';
import Nav from 'react-bootstrap/Nav';
import { LinkContainer } from 'react-router-bootstrap';

const Maps = (props) => (
  <LinkContainer to="/Maps">
    <Nav.Link eventKey="Maps" onSelect={props.navigationToggle}>Home</Nav.Link>
  </LinkContainer>
);

const HowToUse = (props) => (
  <LinkContainer to="/HowToUse">
    <Nav.Link eventKey="HowTouse" onSelect={props.navigationToggle}>How To Use</Nav.Link>
  </LinkContainer>
);

const About = (props) => (
  <LinkContainer to="/About">
    <Nav.Link eventKey="About" onSelect={props.navigationToggle}>About</Nav.Link>
  </LinkContainer>
);

const Navigation = (props) => {
  const [scrolled, setScrolled] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      const isScrolled = window.scrollY > 0;
      setScrolled(isScrolled);
    };

    window.addEventListener('scroll', handleScroll);

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  return (
    <Navbar
      bg={scrolled ? 'transparent' : 'primary'}
      variant="dark"
      fixed="top"
      expand="md"
      className={scrolled ? 'scrolled' : ''}
    >
      <Navbar.Brand href="/" style={{ color: '#000' }}>SKRIPSI</Navbar.Brand>
      <Navbar.Toggle aria-controls="navbar" />
      <Navbar.Collapse id="navbar">
        <Nav className={`ml-auto ${scrolled ? 'text-dark' : ''}`} >
          <Maps navigationToggle={props.navigationToggle} style={{ color: '#000' }}/>
          <HowToUse navigationToggle={props.navigationToggle} />
          <About navigationToggle={props.navigationToggle} />
        </Nav>
      </Navbar.Collapse>
    </Navbar>
  );
};

export default Navigation;
