import React, { useState, useEffect } from 'react';
import Navbar from 'react-bootstrap/Navbar';
import Nav from 'react-bootstrap/Nav';
import { LinkContainer } from 'react-router-bootstrap';

const Maps = (props) => (
  <LinkContainer to="/Maps">
    <Nav.Link eventKey="Maps" onSelect={props.navigationToggle}>Home</Nav.Link>
  </LinkContainer>
);

const HowToUse = (props) => (
  <LinkContainer to="/HowToUse">
    <Nav.Link eventKey="HowTouse" onSelect={props.navigationToggle}>How To Use</Nav.Link>
  </LinkContainer>
);

const About = (props) => (
  <LinkContainer to="/About">
    <Nav.Link eventKey="About" onSelect={props.navigationToggle}>About</Nav.Link>
  </LinkContainer>
);

const Navigation = (props) => {
  const [hideHeader, setHideHeader] = useState(false);

  useEffect(() => {
    // Event listener untuk mendeteksi perubahan posisi scroll
    const handleScroll = () => {
      const scrollPosition = window.scrollY;
      // Tentukan threshold di mana header akan disembunyikan
      const threshold = 100;
      // Ubah state hideHeader berdasarkan posisi scroll
      setHideHeader(scrollPosition > threshold);
    };

    // Tambahkan event listener saat komponen did mount
    window.addEventListener('scroll', handleScroll);

    // Hapus event listener saat komponen akan dibongkar
    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  return (
    <Navbar bg="primary" variant="dark" fixed="top" expand="md" className={hideHeader ? 'hidden' : ''}>
      <Navbar.Brand href="/">SKRIPSI</Navbar.Brand>
      <Navbar.Toggle aria-controls="navbar" />
      <Navbar.Collapse id="navbar">
        <Nav className="ml-auto">
          <Maps navigationToggle={props.navigationToggle} />
          <HowToUse navigationToggle={props.navigationToggle} />
          <About navigationToggle={props.navigationToggle} />
        </Nav>
      </Navbar.Collapse>
    </Navbar>
  );
};

export default Navigation;
