import React, { Component } from 'react';
import { Route, withRouter } from 'react-router-dom';

import Layout from './containers/Layout/Layout';
import Layout2 from './containers/Layout/Layout2';
import MapLayout from './containers/Map/Map';
import HowToUse from './components/HowToUse';
import About from './components/About';

class App extends Component {
  componentDidMount() {
    // Tambahkan event listener saat komponen telah dipasang
    window.addEventListener('scroll', this.handleScroll);
  }

  componentWillUnmount() {
    // Hapus event listener saat komponen akan dihapus
    window.removeEventListener('scroll', this.handleScroll);
  }

  handleScroll = () => {
    const { pathname } = this.props.location;
    const appHeader = document.querySelector('.App-header');

    // Cek apakah kita berada di halaman yang menggunakan layout dengan class .App-header
    if (appHeader && (pathname === '/' || pathname === '/HowToUse' || pathname === '/About')) {
      const scrolledClass = 'scrolled';

      // Jika posisi scroll sudah melewati tinggi header, tambahkan class .scrolled
      if (window.scrollY > appHeader.offsetHeight) {
        appHeader.classList.add(scrolledClass);
      } else {
        appHeader.classList.remove(scrolledClass);
      }
    }
  };

  render() {
    return (
      <Layout>
        <Route path="/" exact component={MapLayout} />
        <Route path="/HowToUse" render={() => <Layout2><HowToUse /></Layout2>} />
        <Route path="/About" render={() => <Layout2><About /></Layout2>} />
      </Layout>
    );
  }
}

export default withRouter(App);
